#include<stdio.h>
 int main() { int n,m,i,j; printf ("enter the number of process"); scanf("%d",&n); printf ("enter the number of blocks"); scanf("%d",&m); int p[n],b[m],allocation[n],a[m]; for(i = 0; i < n; i++) { allocation[i] = -1; } for(i=0;i<n;i++) { printf("Process %d",i); printf("enter the process size"); scanf("%d",&p[i]); } for(i=0;i<m;i++) { printf("Block %d",i); printf("enter the Block size"); scanf("%d",&b[i]); } for (i = 0; i < n; i++) { for (j = 0; j < m; j++) { if ( p[i]<=b[j]) { allocation[i] = j; a[i]=b[j]; b[j] = b[j]-p[i]; break; } } }
printf("\nProcess No.\tProcess Size\tBlock size.\t Block num\n"); for (int i = 0; i < n; i++) { printf(" %d\t\t\t", i); printf("%d\t\t", p[i]); if (allocation[i] != -1) { printf("%d \t",a[i]); printf("%d", allocation[i ]); } else { printf("Not Allocated"); } printf("\n"); } } 
